import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
import Footer from './Footer';
import AIScoreCard from './AIScoreCard';

const App = () => {
  const [activeSection, setActiveSection] = useState('home');
  const [aiData, setAiData] = useState([]);

  const fetchScores = async () => {
    const cachedData = localStorage.getItem('aiScores');
    const cacheTime = localStorage.getItem('cacheTime');
    const now = new Date().getTime();

    // 检查缓存是否存在且未过期（1小时 = 3600000毫秒）
    if (cachedData && cacheTime && (now - cacheTime < 1 * 60 * 60 * 1000)) {
      setAiData(JSON.parse(cachedData));
    } else {
      const response = await fetch('https://aimoralindex.netlify.app/api/scores');
      const data = await response.json();
      setAiData(data);

      // 更新缓存
      localStorage.setItem('aiScores', JSON.stringify(data));
      localStorage.setItem('cacheTime', now);
    }
  };

  useEffect(() => {
    fetchScores();
  }, []);

  return (
    <div className="bg-gray-100 min-h-screen text-gray-900 flex flex-col">
      <Navbar setActiveSection={setActiveSection} />
      <div className="flex-grow">
        {activeSection === 'home' && (
          <div className="p-8 max-w-4xl mx-auto">
            <h1 className="text-3xl font-bold text-center text-gray-800">Generative AI Morality Index</h1>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {aiData.map((entry) => (
                <AIScoreCard 
                  key={entry.date} 
                  name="chatgpt" 
                  score={entry.chatgpt} 
                  history={aiData.map(data => ({ date: data.date, score: data.chatgpt }))} 
                />
              ))}
              {aiData.map((entry) => (
                <AIScoreCard 
                  key={entry.date} 
                  name="claude" 
                  score={entry.claude} 
                  history={aiData.map(data => ({ date: data.date, score: data.claude }))} 
                />
              ))}
              {aiData.map((entry) => (
                <AIScoreCard 
                  key={entry.date} 
                  name="gemini" 
                  score={entry.gemini} 
                  history={aiData.map(data => ({ date: data.date, score: data.gemini }))} 
                />
              ))}
            </div>
          </div>
        )}
        {/* 其他部分 */}
      </div>
      <Footer setActiveSection={setActiveSection} />
    </div>
  );
};

export default App;
